// App.js

import React, { useState } from 'react';
import EmailField from './Components/EmailField';
import PasswordField from './Components/PasswordField';
import LanguageSelector from './Components/LanguageSelector';
import './App.css';

const App = () => {
  const [selectedLanguage, setSelectedLanguage] = useState(getBrowserLanguage());

  return (
    <div className="App">
      <EmailField selectedLanguage={selectedLanguage} />
      <PasswordField selectedLanguage={selectedLanguage} />
      <LanguageSelector setLanguage={setSelectedLanguage} />
    </div>
  );
};

export default App;
